var linkLetter=document.querySelector(".link-letter");if(linkLetter){var modalLetter=document.querySelector(".modal-letter");var nameForm=modalLetter.querySelector("[name=name]");var closeLetter=modalLetter.querySelector(".close");var form=modalLetter.querySelector("form");var emailForm=modalLetter.querySelector("[name=email]");var messageForm=modalLetter.querySelector("[name=message]");linkLetter.addEventListener("click",function(a){a.preventDefault();modalLetter.classList.add("modal-letter-show");nameForm.focus()});closeLetter.addEventListener("click",function(a){a.preventDefault();modalLetter.classList.remove("modal-letter-show")});form.addEventListener("submit",function(a){if(!nameForm.value||!emailForm.value||!messageForm.value){a.preventDefault();console.log("Нужно ввести имя, почту и текст письма")}})}var linkMap=document.querySelector(".map-link");if(linkMap){var modalMap=document.querySelector(".modal-map");var closeMap=modalMap.querySelector(".close-map");linkMap.addEventListener("click",function(a){a.preventDefault();modalMap.classList.add("modal-map-show")});closeMap.addEventListener("click",function(a){a.preventDefault();modalMap.classList.remove("modal-map-show")})}var linkStatus=document.querySelectorAll(".buy-hidden");if(linkStatus){var modalStatus=document.querySelector(".modal-status");var closeStatus=modalStatus.querySelector(".close");linkStatus.forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();modalStatus.classList.add("modal-status-show")})});closeStatus.addEventListener("click",function(a){a.preventDefault();modalStatus.classList.remove("modal-status-show")})};